    function originAddress() external view returns(address);

    /**
     * @dev Get the chain id of the original asset that this synthetic asset represents
     */
    function originChainId() external view returns(uint256);

    /**
     * @dev Get the address of any installed hook on the asset
     */
    function hook() external view returns (IAssetHook);

    /**
     * @dev Add a custom hook to the asset to be called during asset operations. This 
     * can only be called by the asset issuer (company contract)
     */
    function addHook(IAssetHook hook) external;

    /**
     * @dev Remove the custom hook from the asset. Can only be called by the asset
     * issuer (company contract)
     */
    function removeHook() external;

    /**
     * @dev Add a condition to the asset to restrict its use or viewing. This can only be
     * called by the asset issuer (company contract)
     */
    function addCondition(IAssetCondition condition) external;

    /**
     * @dev Remove the condition from the asset. Can only be called by the asset issuer
     * (company contract)
     */
    function removeCondition() external;

    /**
     * @dev Check if the asset can be viewed by the given world, company, experience, and avatar
     * based on the conditions set by the issuer
     */
    function canViewAsset(AssetCheckArgs memory args) external view returns (bool);

    /**
     * @dev Check if the asset can be used by the given world, company, experience, and avatar
     * based on the conditions set by the issuer
     */
    function canUseAsset(AssetCheckArgs memory args) external view returns (bool);

    /**
     * @dev Called by the asset factory to notify the asset that it has been upgraded to a new version
     */
    function upgradeComplete(address nextVersion) external;
}

contracts/asset/IMintableAsset.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.24;

import {IBasicAsset} from './IBasicAsset.sol';

/**
 * @title IMintableAsset
 * @dev All assets in the interoperability layer are synthetic tokens that represent 
 * actual assets on other chains. These assets can only be minted and revoked by an issuing 
 * company after a private verification process has been completed. This interface is common
 * across all assets in the interoperability layer. They can be minted or revoked and checked
 * whether minting can occur.
 * 
 * The actual parameters for minting/revoking are bytes so that any asset can be 
 * represented through this generic interface. We anticipate many asset types being
 * added to the system and therefore left the arguments flexible for future extensibility.
 */
interface IMintableAsset is IBasicAsset {

    /**
     * @dev Returns true if the asset can be minted to the given address with the given data
     */
    function canMint(address to, bytes calldata data) external view returns (bool);

    /**
     * @dev Mints the asset to the given address with the given data. Only callable by the asset issuer
     * after verifying the minting parameters.
     */
    function mint(address to, bytes calldata data) external;

    /**
     * @dev Revokes the asset from the given address with the given data. Only callable by the asset issuer
     */
    function revoke(address holder, bytes calldata data) external;

}

