// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.24;

import {VectorAddress} from '../VectorAddress.sol';


/**
 * @dev Args to register a company in the registry. This is used by the World contract
 * to register a new company.
 */
struct CompanyRegistrationRequest {

    //whether to send any attached tokens to the owner wallet or company contract
    bool sendTokensToCompanyOwner;

    //the address of the company owner
    address owner;

    //the vector address of the company
    VectorAddress vector;

    //initialization data to pass to the company contract
    bytes initData;

    //the name of the company, must be globally unique, case-insensitive
    string name;
}

/**
 * @title ICompanyRegistry
    * @dev Interface for a registry of companies. The registry is responsible for
    * deploying new companies and keeping track of the current version of the company
    * implementations. The registry is also responsible for upgrading companies to a new
    * version.
 */
interface ICompanyRegistry {

    event CompanyRegistered(address indexed company, VectorAddress indexed);

    /**
     * @dev Returns the current version of the company implementation. This is derived
     * from the company factory.
     */
    function currentCompanyVersion() external view returns (uint256);

    /**
     * @dev Sets the company factory that is used to deploy new companies. Only admins 
     * can change this.
     */
    function setCompanyFactory(address factory) external;

    /**
     * @dev Sets the world registry that is used to look up world information. Only admins
     * can change this.
     */
    function setWorldRegistry(address registry) external;

    /**
     * @dev Returns whether an address is a verified company contract.
     */
    function isRegisteredCompany(address company) external view returns (bool);

    /**
     * @dev Registers a new company in the company register. This must be called by a World
     * contract to ensure world authorization is checked.
     */
    function registerCompany(CompanyRegistrationRequest memory request) external payable returns (address);

    /**
     * @dev Upgrades a company to a new version. This is called by the company contract 
     * to ensure authorization is checked prior to upgrading.
     */
    function upgradeCompany(bytes calldata initData) external;
}

contracts/experience/Experience.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.24;

import {IExperience, JumpEntryRequest} from './IExperience.sol';
import {VectorAddress} from '../VectorAddress.sol';
import {IExperienceHook} from './IExperienceHook.sol';
import {ReentrancyGuard} from '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import {IExperienceRegistry} from './IExperienceRegistry.sol';
import {IPortalRegistry} from '../portal/IPortalRegistry.sol';
import {IPortalCondition} from '../portal/IPortalCondition.sol';
import {ExperienceV1Storage, LibExperienceV1Storage} from '../libraries/LibExperienceV1Storage.sol';    
import {BaseProxyStorage, LibBaseProxy, LibProxyAccess} from '../libraries/LibBaseProxy.sol';
import {ICompany} from '../company/ICompany.sol';

/**
 * @dev Arguments for initializing an experience.
 */
struct ExperienceInitData {
    uint256 entryFee;
    bytes connectionDetails;
}

/**
 * @dev Arguments for constructing the master copy of experience contract.
 */
struct ExperienceConstructorArgs {
    address experienceFactory;
    address portalRegistry;
    address experienceRegistry;
}

/**
 * @title Experience
 * @dev implementation of the IExperience interface.
 */
contract Experience is ReentrancyGuard, IExperience {

    using LibProxyAccess for BaseProxyStorage;

    //initialized when deploying master copy
    address public immutable experienceFactory;
    IPortalRegistry public immutable portalRegistry;
    IExperienceRegistry public immutable experienceRegistry;
    uint256 public constant override version = 1;

    modifier onlyFactory() {
        require(msg.sender == experienceFactory, "Experience: caller is not the factory");
        _;
    }

    modifier onlyPortalRegistry() {
        require(msg.sender == address(portalRegistry), "Experience: caller is not the portal registry");
        _;
    }

    modifier onlyRegistry {
        require(msg.sender == address(experienceRegistry), "Experience: caller is not the registry");
        _;
    }



    modifier onlyCompany() {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        require(msg.sender == address(s.company), "Experience: caller is not the company");
        _;
    }

    constructor(ExperienceConstructorArgs memory args) {
        require(args.experienceFactory != address(0), "Experience: zero address factory");
        require(args.portalRegistry != address(0), "Experience: zero address portalRegistry");
        require(args.experienceRegistry != address(0), "Experience: zero address experienceRegistry");
        experienceFactory = args.experienceFactory;
        portalRegistry = IPortalRegistry(args.portalRegistry);
        experienceRegistry = IExperienceRegistry(args.experienceRegistry);
    }

    //all fees go to the company
    receive() external payable {

    }

    /**
     * @inheritdoc IExperience
     */
    function init(address _company, string memory _name, VectorAddress memory vector, bytes memory initData) external onlyFactory override {
        //these args are passed through from the registry. The company initiated the 
        //registration so it's passed from the registry as the caller. The vector address
        //is generated by the company contract before passing to registry. The other args
        //are provided off chain as part of the registration process.
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        require(address(s.company) == address(0), "Experience: already initialized");

        s.company = ICompany(_company);
        ExperienceInitData memory data = abi.decode(initData, (ExperienceInitData));
        s.vectorAddress = vector;
        s.world = s.company.world();
        s.name = _name;
        s.entryFee = data.entryFee;
        s.connectionDetails = data.connectionDetails;
    }

    /**
      * @dev convenience function to encode the init data for the experience.
     */
    function encodeInitData(ExperienceInitData memory data) external pure returns (bytes memory) {
        return abi.encode(data);
    }

    /**
     * @inheritdoc IExperience
     */
    function world() external view returns (address) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return s.world;
    }

    /**
     * @inheritdoc IExperience
     */
    function name() external view returns (string memory) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return s.name;
    }

    /**
     * @inheritdoc IExperience
     */
    function entryFee() external view returns (uint256) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return s.entryFee;
    }

    /**
     * @inheritdoc IExperience
     */
    function company() external view override returns (address) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return address(s.company);
    }

    /**
     * @inheritdoc IExperience
     */
    function vectorAddress() external view override returns (VectorAddress memory) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return s.vectorAddress;
    }

    /**
     * @inheritdoc IExperience
     */
    function connectionDetails() external view returns (bytes memory) {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        return s.connectionDetails;
    }

    /**
     * @inheritdoc IExperience
     */
    function addHook(IExperienceHook _hook) external override onlyCompany {
        require(address(_hook) != address(0), "Experience: hook zero address");
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        s.hook = _hook;
        emit HookAdded(address(_hook));
    }

    /**
     * @inheritdoc IExperience
     */
    function removeHook() external override onlyCompany {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        require(address(s.hook) != address(0), "Experience: hook not set");
        address a = address(s.hook);
        emit HookRemoved(a);
        delete s.hook;
    }

    /**
     * @inheritdoc IExperience
     */
    function addPortalCondition(IPortalCondition condition) public onlyCompany {
        portalRegistry.addCondition(condition);
    }

    /**
     * @inheritdoc IExperience
     */
    function removePortalCondition() public onlyCompany {
        portalRegistry.removeCondition();
    }

    /**
     * @inheritdoc IExperience
     */
    function changePortalFee(uint256 fee) public onlyCompany {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        s.entryFee = fee;
        portalRegistry.changePortalFee(fee);
        emit PortalFeeChanged(fee);
    }

    /**
     * @inheritdoc IExperience
     */
    function entering(JumpEntryRequest memory request) external payable override nonReentrant onlyPortalRegistry returns (bytes memory)  {
        ExperienceV1Storage storage s = LibExperienceV1Storage.load();
        if(address(s.hook) != address(0)) {
            bool ok = s.hook.beforeJumpEntry(address(this), request.sourceWorld, request.sourceCompany, request.avatar);
            require(ok, "Experience: hook disallowed entry");
        }
        payable(address(s.company)).transfer(msg.value);
        return s.connectionDetails;
    }

    /**
     * @inheritdoc IExperience
     */
    function upgrade(bytes memory initData) external override onlyCompany {
        experienceRegistry.upgradeExperience(initData);
    }

    /**
     * @inheritdoc IExperience
     */
    function upgradeComplete(address nextVersion) public override onlyFactory {
        BaseProxyStorage storage ps = LibBaseProxy.load();
        address old = ps.implementation;
        ps.implementation = nextVersion;
        emit ExperienceUpgraded(old, nextVersion);
    }
}

contracts/experience/IExperience.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.24;

import {VectorAddress} from '../VectorAddress.sol';
import {IExperienceHook} from './IExperienceHook.sol';
import {IPortalCondition} from '../portal/IPortalCondition.sol';

/**
 * @dev Entry request when an avatar jumps into an experience
 */
struct JumpEntryRequest {
    address sourceWorld;
    address sourceCompany;
    address avatar;
}

/**
 * @dev Interface for an experience. An experience is something a Company offers within a 
 * World. Avatars portal into experiences to interact with them. Portaling into an Experience
 * may incur a fee paid by the Avatar. An Experience may have hooks that are called when an
 * Avatar enters the experience that can further evaluate the request outside of Portal conditions.
 */
interface IExperience {
    event JumpEntry(address indexed sourceWorld, address indexed sourceCompany, address indexed avatar, uint256 attachedFees);
    event HookAdded(address indexed hook);
    event HookRemoved(address indexed hook);
    event ExperienceUpgraded(address indexed oldVersion, address indexed newVersion);
    event PortalFeeChanged(uint256 newFee);

    /**
     * @dev Returns the version of the experience contract. Can be compared with factory
     * supported versions to determine if an upgrade is needed.
     */
    function version() external view returns (uint256);

    /**
     * @dev Returns the company that controls this experience
     */
    function company() external view returns (address);

    /**
     * @dev Returns the world that this experience is in
     */
    function world() external view returns (address);

    /**
     * @dev Returns the spatial vector address for this experience, which is derived
